class UnionFind:
    def __init__(self,n):
        self.count = n
        self.parent = [i for i in range(n)]
#        self.rank = [1 for _ in range(n)]
    def find(self,p):
        root = p
        while root!=self.parent[root]:
            root = self.parent[root]
        while p!=self.parent[p]:
            tmp = self.parent[p]
            self.parent[p] = root
            p = tmp
        return root
    def union(self,p,q):
        p_root = self.find(p)
        q_root = self.find(q)
        if p_root==q_root:
            return
        self.parent[p_root] = q_root
        self.count -= 1
        
    
class Solution:
    def numSimilarGroups(self, A):
        if not A:
            return 0
        n = len(A)
        uf = UnionFind(n)
        for i in range(n):
            word1 = A[i]
            for j in range(i+1,n):
                word2 = A[j]
                if self.is_similar(word1,word2):
                    uf.union(i,j)
#                    break
        return uf.count

    def is_similar(self,word1,word2):
        diff = 0
        n = len(word1)
        for i in range(n):
            if word1[i]!=word2[i]:
                diff +=1
        return diff<=2
        
        
        
